import React from 'react';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import Button from '@material-ui/core/Button';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import SkipPreviousIcon from '@material-ui/icons/SkipPrevious';
import PlayArrowIcon from '@material-ui/icons/PlayArrow';
import SkipNextIcon from '@material-ui/icons/SkipNext';
import clsx from 'clsx';
import Collapse from '@material-ui/core/Collapse';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Highlighter from "react-highlight-words";
import ShowMoreText from 'react-show-more-text';
import Stars from 'react-stars-display';

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    marginTop: '5%',
    marginBottom: '5%',
    border: '0.2em solid lightgrey'
  },
  cover: {
    maxWidth: 120,
    minWidth: 120,
    padding: 7,
    objectFit: 'cover',
  },
  details: {
    display: 'flex',
    flexDirection: 'column',
  },
  content: {
  },
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  }
}));

export default function BookCard({resultObj, searchTerms}) {
    const classes = useStyles();
    const [expanded, setExpanded] = React.useState(false);

    let book  = resultObj;
    let quote = resultObj.quote ?  resultObj.quote : "";
    let quoteArr = quote.split(" ");
    let authors = ""
    for (let i = 0; i < book.authors.length; i++) {
      if (i == book.authors.length - 1) {
        authors += book.authors[i]
      } else {
        authors += book.authors[i] + ", "
      }
    }

    const handleExpandClick = () => {
      setExpanded(!expanded);
    };


    return (

      <Card className={classes.root} >
        <CardMedia
          className={classes.cover}
        >
          <img src={book.thumbnail} style={{"max-height" : "100%", "max-width" : "100%", 
          "border" : "0.2em solid black", "border-radius" : "0.1em"}}/>
        </CardMedia>
        <div className={classes.details}>

          <CardContent className={classes.content}>
            <Typography variant="h5" component="h2">
              {book.title}
            </Typography>
            <Typography gutterBottom component="h3"   >
              {authors}
            </Typography>

            <Typography gutterBottom component="h4" variant="h6" style={{"color" : "darkblue", "font-style": "italic"}} >

            {(quote.length > 300 ? 
            <ShowMoreText
            /* Default options */
            lines={3}
            more='Show more'
            less='Show less'
            className='content-css'
            anchorClass='my-anchor-css-class'
            expanded={false}
            width={0}
            >
              <q cite="https://www.mozilla.org/en-US/about/history/details/">{
            <Highlighter
            highlightClassName="YourHighlightClass"
            searchWords={searchTerms}
            autoEscape={true}
            textToHighlight={quote}
          >

            </Highlighter>}</q>
            </ShowMoreText>
            : 
            <q cite="https://www.mozilla.org/en-US/about/history/details/">{
            <Highlighter
            highlightClassName="YourHighlightClass"
            searchWords={searchTerms}
            autoEscape={true}
            textToHighlight={quote}
            >
            </Highlighter>}</q>
            )}



            </Typography>
            <Button disableRipple size="small" color="primary" href={book.previewLink}  target="_blank" style={{'cursor' : 'pointer',  
              'textTransform': "none", 'backgroundColor': 'transparent'  }}>
              View on Google Books
            </Button>
            <hr  style={{
              color: 'lightblue',
              align: 'left',
              margin: 0,
              width: '100%'
            }}/>
          </CardContent>

          <IconButton
          className={clsx(classes.expand, {
            [classes.expandOpen]: expanded,
          })}
          onClick={handleExpandClick}
          aria-expanded={expanded}
          aria-label="show more"
          >
            <ExpandMoreIcon />
          </IconButton>
          <Collapse in={expanded} timeout="auto" unmountOnExit style={{"marginTop" : "-10%"}}>
            <CardContent>
              <Typography gutterBottom component="h3"   >
              • Book categories: {book.categories}
              </Typography>
              <Typography gutterBottom component="h3"   >
              • Number of pages: {book.pageCount}
              </Typography>
              <Typography gutterBottom component="h3"   >
              • Published date: {book.publishedDate}
              </Typography>
              <Typography gutterBottom component="h3"   >
              • ISBN: {book['isbn-13']}
              </Typography>
              <Typography gutterBottom component="h3"   >
              • Average rating: <Stars
                                stars={book.averageRating}
                                size={15} //optional
                                spacing={2} //optional
                                fill='#ea9c46' //optional
                                />
              </Typography>
              <Typography gutterBottom component="h3"   >
              • Number of ratings: {book.ratingsCount}
              </Typography>
            </CardContent>
          </Collapse>
        </div>
      </Card>

    )              
}
