import React from 'react';


import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';


const useStyles = makeStyles((theme) => ({
    cardGrid: {
      paddingTop: theme.spacing(2),
      paddingBottom: theme.spacing(2),
    },
    card: {
      height: '100%',
      display: 'flex',
      flexDirection: 'column',
      width: '100%'
    },
    cardMedia: {
      align: "left",
      height: '10%',
      paddingTop: '100%',
    },
    cardContent: {
      flexGrow: 1,
    },

}));


export default function BookCard({resultObj}) {
    // console.log("Book card",resultObj)
    const classes = useStyles();
    let book  = resultObj;
    let quote = resultObj.quote ?  resultObj.quote : "" //resultObj.quote.quoteStr;
    let authors = ""
    for (let i = 0; i < book.authors.length; i++) {
      if (i == book.authors.length - 1) {
        authors += book.authors[i]
      } else {
        authors += book.authors[i] + ", "
      }
    }
    // console.log(book)
    // console.log(quote)

    return (
    <Grid 
    item key={book._id}  
    className={classes.cardGrid}

    // md={4}
    >
        <Card raised className={classes.card}>
        <CardMedia
            className={classes.cardMedia}
            image = {book.thumbnail}
        />
        <CardContent className={classes.cardContent}>
            <Typography variant="h4" component="h2">
            {book.title}
            </Typography>
            <Typography gutterBottom component="h3"  style={{"font-style": "italic"}} >
             {authors}
            </Typography>

            <Typography component="h4">
            {quote}
            </Typography>
        </CardContent>
        <CardActions>
            <Button size="small" color="primary" href={book.previewLink}  target="_blank">
            View on Google Books
            </Button>
            <Button size="small" color="primary">
            Edit
            </Button>
        </CardActions>
        </Card>
      </Grid>
    )              
  }
